  // 6 = dirt (no porosity)
  function setup() {
    createCanvas(canvasSize, canvasSize);

    grid = newGrid(0);
    fluid = newGrid(1);
    noStroke();
    frameRate(20);
  }

  function draw() {
    if(pause)return;
    if(!frame && !nextFrame)nextFrame=true;
    if(!nextFrame)return;
    newFluid = newGrid(1);
    for (let y = cellCount - 1; y >= 0; y--) {
      p: for (let x = 0; x < cellCount; x++) {
          let cId = fluid[x][y];
          if(cId!=1 && cId!=2) continue;

          let options = [];
          let onLeft = cellCount/2 > x;
          let pDown = flowProb(x, y+1, cId, newFluid);
          if(pDown==1) {
            newFluid[x][y+1]=cId;
            continue p;
          }
          if (pDown > 0) options.push({px:pDown, dx:0, dy:1});
          let pDL = flowProb(x-1, y+1, cId, newFluid);
          if (pDL > 0) {
            if(onLeft)pDL-=(cId==1?0.005:0.0001);
            else pDL+=(cId==1?0.005:0.0001);
            options.push({px:pDL, dx:-1, dy:1});
          }
          let pDR = flowProb(x+1, y+1, cId, newFluid);
          if (pDR > 0) {
            if(onLeft)pDR+=(cId==1?0.005:0.0001);
            else pDR-=(cId==1?0.005:0.0001);
            options.push({px:pDR, dx:1, dy:1});
          }

          options.sort((a, b) => b.px - a.px);

          for(let op of options){
            if(random(1)<op.px){
              newFluid[x+op.dx][y+op.dy] = cId;
              continue p;
            }
          }

          let pR = flowProb(x+1,y,cId,newFluid) + (onLeft?0.008 * (pDR+pDL+pDown===0?5:1):-0.005);
          let pL = flowProb(x-1,y,cId,newFluid) + (onLeft?-0.005:0.008 * (pDR+pDL+pDown===0?5:1));;
          if(cId==1 && random(1)<pR && random(1)<0.1 && fluid[x+1][y]==0)
            newFluid[x+1][y] = cId;
          else if(cId==1 && random(1)<pL && random(1)<0.1 && fluid[x-1][y]==0)
            newFluid[x-1][y]=cId;
          else
            newFluid[x][y] = cId;
      }
    }

    fluid=newFluid;

    spawnFluid();

    background(0);
    for(let i = 0; i<cellCount; i++){
      for(let j = 0; j<cellCount; j++){
        if(fluid[i][j]!==0 && grid[i][j]==5){
          
          fluid[i][j]=0;
        }
        if(fluid[i][j]==1) // water
          fill(0,150,250);
        else if(fluid[i][j]==2) // mp
          fill(230,100,100);

        else if(grid[i][j]==3) // gravel
          fill(200,200,240);
        else if(grid[i][j]==4) // mesh
          fill(20,200,100);
        else if(grid[i][j]==5) // goal
          fill(200,200,50);
        else if(grid[i][j]==6) // dirt
          fill(99, 68, 32);

        else
          fill(0);
        square(i*(cellSize), j*(cellSize), cellSize);
      }
    }
    if(frame)
      nextFrame=false;
  }

  function flowProb(x, y, id, nextFluid){
    if(x>=cellCount || x<0 || y>=cellCount || y<0)
      return 0;

    let gId = grid[x][y];
    let fId = nextFluid[x][y];

    if(fId != 0) return 0;
    if(gId == 0) return 1; //open
    if(gId == 6) return 0; //dirt
    if(gId == 5) return 1; //goal

    if(id==1) { //water
      if(gId == 3) return 0.01; //gravel
      if(gId == 4) return 0.004; //mesh
    }
    else if(id==2) { //mp
      if(gId == 3) return 0.001; //gravel
      if(gId == 4) return 0.0002; //mesh
    }

    return 0;
  }

  function spawnFluid() {
    if(!mouseIsPressed)return;
    let mouseCol = floor(mouseX / cellSize);
    let mouseRow = floor(mouseY / cellSize);

    let matrix = brushSize;
    let extent = floor(matrix / 2);
    for (let i = -extent; i <= extent; i++) {
      for (let j = -extent; j <= extent; j++) {
        let col = mouseCol + i;
        let row = mouseRow + j;
        if (col>=0 && col < cellCount && row>=0 && row < cellCount) {
          if(fluid[col][row]==0 && random(1)<brushAcc){
            // if(!isNaN(key)) //drawing
            //   grid[col][row] = int(key);
            let water = random(1) < 0.9;
            fluid[col][row] = water?1:2;



          }
        }
      }
    }
  }

  function keyPressed(){
    if(key==='r'){
      grid=JSON.parse(gridString);
      fluid=newGrid(1);
      resetCounters();
    }
    if(key=="t"){
      for(let i = 0; i<cellCount; i++){
        for(let j = 0; j<cellCount; j++){
          if(grid[i][j]==pKey)
            grid[i][j]=0;
        }
      }
    }
    if(key==='p')
      pause=!pause;

    if(key==="e")
      exportGrid();

    if(key==="f")
      frame=!frame;
    if(key==="g")
      nextFrame=true;

    if(key === "[") {
      brushSize-=1;
      print(brushSize);
    }
    else if(key==="]"){
      brushSize+=1;
      print(brushSize);
    }
    if(key === "-") {
      brushAcc-=0.05;
      print(brushAcc);
    }
    else if(key==="="){
      brushAcc+=0.05;
      print(brushAcc);
    }

    pKey = key;
  }

  function newGrid(i){
    if(i==0)
      return JSON.parse(gridString);

    grimd = new Array(cellCount);
    for(let i = 0; i<cellCount; i++){
      grimd[i] = new Array(cellCount);
      for(let j = 0; j<cellCount; j++)
        grimd[i][j] = 0; 
    }
    return grimd;
    
  }
  
  function exportGrid() {
    let gridJSON = JSON.stringify(grid);
    let blob = new Blob([gridJSON], {type: "application/json"});
    let url = URL.createObjectURL(blob);

    let a = document.createElement("a");
    a.href = url;
    a.download = "grid.json";
    a.click();

    URL.revokeObjectURL(url);
    console.log("mmmb");
  }


